[T638458 忙碌的老师](https://www.luogu.com.cn/problem/T638458?contestId=271047)

```cpp
#include<iostream>
#include<vector>
#include<queue>
#include<algorithm>
using namespace std;
int n,cnt=1;
vector<pair<int,int>>a;
priority_queue<int,vector<int>,greater<int>>qp;
int main(){
	ios::sync_with_stdio(0);
	cin.tie(nullptr);
	cin>>n;
	a.resize(n);
	for(auto &&i:a){
		cin>>i.first>>i.second;
	}
	sort(a.begin(),a.end());
	qp.push(a[0].second);
	for(int i=1;i<n;i++){
		if(qp.top()>=a[i].first){
			qp.push(a[i].second);
			cnt++;
		}else{
			qp.pop();
			qp.push(a[i].second);
		}
	}
	cout<<cnt;
	return 0;
}
```
